import React, { useEffect, useState } from "react";
import API from "../services/api";
import axios from "axios";

// Fetch coins list from CoinGecko
const fetchCoinsList = async () => {
  try {
    const res = await axios.get(
      "https://api.coingecko.com/api/v3/coins/list"
    );
    return res.data;
  } catch (err) {
    console.error("Error fetching coins list:", err);
    return [];
  }
};

export default function Wallet() {
  const [wallets, setWallets] = useState([]);
  const [coinsList, setCoinsList] = useState([]);
  const [selectedCoin, setSelectedCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  // Load wallets and coin prices
  useEffect(() => {
    const fetchWallets = async () => {
      if (!token) {
        setMessage("❌ Please login first");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setMessage("");

        // Fetch wallets from backend
        const res = await API.get("/wallets", {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Fetch live prices from CoinGecko
        const updatedWallets = await Promise.all(
          res.data.map(async (w) => {
            let valueUSD = "N/A";
            if (w.coin) {
              try {
                const coinRes = await axios.get(
                  `https://api.coingecko.com/api/v3/simple/price?ids=${w.coin.toLowerCase()}&vs_currencies=usd`
                );
                valueUSD = coinRes.data[w.coin.toLowerCase()]?.usd
                  ? (coinRes.data[w.coin.toLowerCase()].usd * w.amount).toFixed(2)
                  : "N/A";
              } catch {
                valueUSD = "N/A";
              }
            }
            return { ...w, valueUSD };
          })
        );

        setWallets(updatedWallets);
      } catch (err) {
        console.error(err);
        setMessage("❌ Failed to fetch wallet data");
      } finally {
        setLoading(false);
      }
    };

    fetchWallets();
  }, [token]);

  // Load coins list for dropdown
  useEffect(() => {
    const loadCoins = async () => {
      const data = await fetchCoinsList();
      setCoinsList(data);
    };
    loadCoins();
  }, []);

  // Add new wallet/coin
  const handleAdd = async (e) => {
    e.preventDefault();
    if (!selectedCoin || !amount) return;

    try {
      await API.post(
        "/wallets",
        { coin: selectedCoin, amount: Number(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSelectedCoin("");
      setAmount("");

      // Refresh wallet list
      const res = await API.get("/wallets", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedWallets = await Promise.all(
        res.data.map(async (w) => {
          let valueUSD = "N/A";
          if (w.coin) {
            try {
              const coinRes = await axios.get(
                `https://api.coingecko.com/api/v3/simple/price?ids=${w.coin.toLowerCase()}&vs_currencies=usd`
              );
              valueUSD = coinRes.data[w.coin.toLowerCase()]?.usd
                ? (coinRes.data[w.coin.toLowerCase()].usd * w.amount).toFixed(2)
                : "N/A";
            } catch {
              valueUSD = "N/A";
            }
          }
          return { ...w, valueUSD };
        })
      );

      setWallets(updatedWallets);
    } catch (err) {
      console.error(err);
      setMessage("❌ Failed to add wallet");
    }
  };

  if (loading) return <p>Loading wallet data...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>My Crypto Wallet</h2>
      {message && <p style={{ color: "red" }}>{message}</p>}

      <form onSubmit={handleAdd} style={{ marginBottom: "20px" }}>
        <select
          value={selectedCoin}
          onChange={(e) => setSelectedCoin(e.target.value)}
          required
        >
          <option value="">Select Coin</option>
          {coinsList.map((coin) => (
            <option key={coin.id} value={coin.id}>
              {coin.name} ({coin.symbol.toUpperCase()})
            </option>
          ))}
        </select>

        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          Add Coin
        </button>
      </form>

      <h3>Your Holdings:</h3>
      {wallets.length === 0 ? (
        <p>No coins added yet</p>
      ) : (
        <table border="1" cellPadding="5">
          <thead>
            <tr>
              <th>Coin</th>
              <th>Amount</th>
              <th>Value (USD)</th>
            </tr>
          </thead>
          <tbody>
            {wallets.map((w) => (
              <tr key={w._id || w.coin}>
                <td>{w.coin || "N/A"}</td>
                <td>{w.amount || 0}</td>
                <td>{w.valueUSD}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
