import React, { useState } from "react";
import API from "../services/api";

export default function Bank() {
  const [coin, setCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");

  const handleAddCoin = async (e) => {
    e.preventDefault();
    setMessage("");
    if (!coin || !amount) {
      setMessage("‚ùå Please select coin and enter amount");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      const res = await API.post("/wallets/bank-add", { coin, amount }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage(res.data.message || "‚úÖ Coin added to wallet!");
      setCoin("");
      setAmount("");
    } catch (err) {
      setMessage(err.response?.data?.message || "‚ùå Failed to add coin");
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: "40px auto", padding: 20, border: "1px solid #eee", borderRadius: 10 }}>
      <h2>üè¶ Bank: Add Coin to Wallet</h2>
      {message && <p style={{ color: message.startsWith("‚ùå") ? "red" : "green" }}>{message}</p>}
      <form onSubmit={handleAddCoin} style={{ display: "flex", flexDirection: "column", gap: 16 }}>
        <select value={coin} onChange={e => setCoin(e.target.value)} required style={{ padding: 10, borderRadius: 5 }}>
          <option value="">Select Coin</option>
          <option value="BTC">Bitcoin (BTC)</option>
          <option value="ETH">Ethereum (ETH)</option>
          <option value="USDT">Tether (USDT)</option>
          <option value="BNB">Binance Coin (BNB)</option>
          <option value="ADA">Cardano (ADA)</option>
          <option value="SOL">Solana (SOL)</option>
        </select>
        <input type="number" min="0.01" step="0.01" value={amount} onChange={e => setAmount(e.target.value)} placeholder="Amount" required style={{ padding: 10, borderRadius: 5 }} />
        <button type="submit" style={{ padding: 10, background: "#4caf50", color: "#fff", border: "none", borderRadius: 5, fontWeight: "bold" }}>Add Coin</button>
      </form>
    </div>
  );
}
