import { useState, useEffect } from "react";
import API from "../services/api";
import { Bell as BellIcon } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";

export default function NotificationBell({ token, userId }) {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showDropdown, setShowDropdown] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const tokenLocal = token || localStorage.getItem("token");
    if (!tokenLocal) return;

    const fetchNotifications = async () => {
      try {
        const res = await API.get("/notifications", {
          headers: { Authorization: `Bearer ${tokenLocal}` },
        });
        setNotifications(res.data || []);
        setUnreadCount((res.data || []).filter(n => !n.isRead).length);
      } catch (err) {
        console.error("Error fetching notifications:", err);
      }
    };

    fetchNotifications();

    const backendOrigin = API.defaults.baseURL?.replace(/\/api\/?$/, "") || "http://localhost:5000";
    const socket = io(backendOrigin);

    socket.on("connect", () => {
      let uid = userId || JSON.parse(localStorage.getItem("user"))?._id;
      if (uid) socket.emit("identify", uid);
    });

    socket.on("newNotification", notification => {
      setNotifications(prev => [notification, ...prev]);
      setUnreadCount(prev => prev + 1);
    });

    return () => socket.disconnect();
  }, [token, userId]);

  const markAsReadAndRedirect = async (n) => {
    try {
      if (!n.isRead) {
        await API.put(`/notifications/${n._id}/read`, null, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setNotifications(prev =>
          prev.map(item => (item._id === n._id ? { ...item, isRead: true } : item))
        );
        setUnreadCount(prev => Math.max(0, prev - 1));
      }

      // Redirect to transaction page
      navigate("/transactions"); // adjust this route if needed
    } catch (err) {
      console.error("Error marking notification as read:", err);
    }
  };

  return (
    <div className="relative">
      <BellIcon
        className="w-6 h-6 text-gray-700 cursor-pointer"
        onClick={() => setShowDropdown(!showDropdown)}
      />

      {unreadCount > 0 && (
        <span className="absolute top-0 right-0 bg-red-500 text-white rounded-full w-4 h-4 flex items-center justify-center text-xs">
          {unreadCount}
        </span>
      )}

      {showDropdown && (
        <div className="absolute right-0 mt-2 w-64 bg-white shadow-lg border rounded-md z-50 max-h-64 overflow-y-auto">
          {notifications.length === 0 ? (
            <p className="p-2 text-sm text-gray-500">No notifications yet</p>
          ) : (
            notifications.map(n => (
              <div
                key={n._id}
                className={`p-2 text-sm cursor-pointer hover:bg-gray-100 ${
                  !n.isRead ? "font-bold bg-gray-50" : ""
                }`}
                onClick={() => markAsReadAndRedirect(n)}
              >
                {n.message}
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}
