import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Transactions() {
  const [tab, setTab] = useState("send");
  const [coin, setCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [recipient, setRecipient] = useState("");
  const [transactions, setTransactions] = useState([]);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const res = await axios.get("/api/transactions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "/api/transactions",
        { type: tab === "send" ? "Send" : "Receive", coin, amount, recipient },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message || "Transaction successful!");
      setAmount("");
      setCoin("");
      setRecipient("");
      fetchTransactions();
    } catch (err) {
      console.error(err);
      setMessage("Transaction failed. Please try again.");
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4 text-yellow-500">ðŸ’¸ Transactions</h1>

      {/* Tabs */}
      <div className="flex mb-6">
        <button
          onClick={() => setTab("send")}
          className={`px-6 py-2 rounded-l-lg ${
            tab === "send" ? "bg-blue-600 text-white" : "bg-gray-200 text-black"
          }`}
        >
          Send
        </button>
        <button
          onClick={() => setTab("receive")}
          className={`px-6 py-2 rounded-r-lg ${
            tab === "receive" ? "bg-green-600 text-white" : "bg-gray-200 text-black"
          }`}
        >
          Receive
        </button>
      </div>

      {/* Form */}
      <form
        onSubmit={handleSubmit}
        className="bg-white shadow-lg rounded-lg p-6 w-full md:w-1/2 mb-8"
      >
        <label className="block mb-2 font-semibold">Coin Name</label>
        <input
          type="text"
          value={coin}
          onChange={(e) => setCoin(e.target.value)}
          placeholder="Enter coin name (e.g., Bitcoin)"
          className="w-full border rounded p-2 mb-4"
          required
        />

        <label className="block mb-2 font-semibold">Amount</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Enter amount"
          className="w-full border rounded p-2 mb-4"
          required
        />

        {tab === "send" && (
          <>
            <label className="block mb-2 font-semibold">Recipient Email</label>
            <input
              type="email"
              value={recipient}
              onChange={(e) => setRecipient(e.target.value)}
              placeholder="Enter recipient email"
              className="w-full border rounded p-2 mb-4"
              required
            />
          </>
        )}

        <button
          type="submit"
          className={`w-full py-2 rounded text-white ${
            tab === "send" ? "bg-blue-600 hover:bg-blue-700" : "bg-green-600 hover:bg-green-700"
          }`}
        >
          {tab === "send" ? "Send Coin" : "Receive Coin"}
        </button>
      </form>

      {message && <p className="text-center mb-6 text-lg text-gray-700">{message}</p>}

      {/* Transaction History */}
      <h2 className="text-2xl font-semibold mb-4">ðŸ“œ Transaction History</h2>
      {transactions.length === 0 ? (
        <p>No transactions yet.</p>
      ) : (
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">Date</th>
              <th className="border px-4 py-2">Type</th>
              <th className="border px-4 py-2">Coin</th>
              <th className="border px-4 py-2">Amount</th>
              <th className="border px-4 py-2">Recipient</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((tx) => (
              <tr key={tx._id}>
                <td className="border px-4 py-2">{new Date(tx.date).toLocaleString()}</td>
                <td className="border px-4 py-2">{tx.type}</td>
                <td className="border px-4 py-2">{tx.coin}</td>
                <td className="border px-4 py-2">{tx.amount}</td>
                <td className="border px-4 py-2">{tx.recipient || "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
