import { useState, useEffect } from "react";
import API from "../services/api";
import { Bell as BellIcon } from "lucide-react";
import { io } from "socket.io-client";

export default function NotificationBell({ token, userId }) {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    // Get token from props or localStorage
    const tokenLocal = token || localStorage.getItem("token");
    if (!tokenLocal) return; // Early exit if no token

    // Fetch notifications
    const fetchNotifications = async () => {
      try {
        const res = await API.get("/notifications");
        const data = res.data || [];
        setNotifications(data);
        setUnreadCount(data.filter((n) => !n.isRead).length);
      } catch (err) {
        console.error("Error fetching notifications:", err);
      }
    };

    fetchNotifications();

    // Determine backend URL for Socket.IO
    const backendOrigin =
      (API.defaults?.baseURL?.replace(/\/api\/?$/, "") || "http://localhost:5000");

    const socket = io(backendOrigin);

    socket.on("connect", () => {
      let uid = userId;
      if (!uid) {
        try {
          const u = localStorage.getItem("user");
          uid = u ? JSON.parse(u)._id || JSON.parse(u).id : null;
        } catch {
          uid = null;
        }
      }
      if (uid) socket.emit("identify", uid);
    });

    // Listen for new notifications
    socket.on("newNotification", (notification) => {
      setNotifications((prev) => [notification, ...prev]);
      setUnreadCount((prev) => prev + 1);
    });

    return () => {
      socket.disconnect();
    };
  }, [token, userId]);

  // Mark a notification as read
  const markAsRead = async (id) => {
    try {
      await API.put(`/notifications/${id}/read`);
      setNotifications((prev) =>
        prev.map((n) => (n._id === id ? { ...n, isRead: true } : n))
      );
      setUnreadCount((prev) => Math.max(0, prev - 1));
    } catch (err) {
      console.error("Error marking notification as read:", err);
    }
  };

  return (
    <div className="relative">
      {/* Bell Icon always rendered */}
      <BellIcon
        className="w-6 h-6 text-gray-700 cursor-pointer"
        onClick={() => setShowDropdown(!showDropdown)}
      />

      {/* Unread Badge only if count > 0 */}
      {unreadCount > 0 && (
        <span className="absolute top-0 right-0 bg-red-500 text-white rounded-full w-4 h-4 flex items-center justify-center text-xs">
          {unreadCount}
        </span>
      )}

      {/* Dropdown List */}
      {showDropdown && (
        <div className="absolute right-0 mt-2 w-64 bg-white shadow-lg border rounded-md z-50 max-h-64 overflow-y-auto">
          {notifications.length === 0 ? (
            <p className="p-2 text-sm text-gray-500">No notifications</p>
          ) : (
            notifications.map((n) => (
              <div
                key={n._id}
                className={`p-2 text-sm cursor-pointer hover:bg-gray-100 ${
                  !n.isRead ? "font-bold bg-gray-50" : ""
                }`}
                onClick={() => markAsRead(n._id)}
              >
                {n.message}
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}
