// src/pages/Transactions.js
import React, { useEffect, useState, useCallback } from "react";
import API from "../services/api";

export default function Transactions() {
  const [tab, setTab] = useState("send");
  const [wallets, setWallets] = useState([]);
  const [recipientEmail, setRecipientEmail] = useState("");
  const [selectedCoin, setSelectedCoin] = useState("");
  const [sendAmount, setSendAmount] = useState("");
  const [receivedTx, setReceivedTx] = useState([]);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");
  const userEmail = localStorage.getItem("userEmail");

  // Redirect if user is not logged in
  useEffect(() => {
    if (!userEmail || !token) {
      setMessage("‚ùå User not logged in");
    }
  }, [userEmail, token]);

  // Fetch wallets
  const fetchWallets = useCallback(async () => {
    if (!token) return;
    try {
      const res = await API.get("/wallets", { headers: { Authorization: `Bearer ${token}` } });
      setWallets(res.data);
    } catch (err) {
      console.error("‚ùå Wallet fetch error:", err.response?.data || err.message);
    }
  }, [token]);

  // Fetch received transactions
  const fetchReceived = useCallback(async () => {
    if (!token) return;
    try {
      const res = await API.get("/transactions/received", { headers: { Authorization: `Bearer ${token}` } });
      setReceivedTx(res.data);
    } catch (err) {
      console.error("‚ùå Received fetch error:", err.response?.data || err.message);
    }
  }, [token]);

  useEffect(() => {
    fetchWallets();
    fetchReceived();
  }, [fetchWallets, fetchReceived]);

  // ============================
  // Debug function: Verify recipient
  // ============================
  const verifyRecipient = async (email) => {
    try {
      console.log("üîπ Verifying recipient:", email);
      console.log("üîπ Using token:", token);

      const res = await API.post(
        "/transactions/verify-user",
        { email },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("üîπ Backend response:", res.data);
      return res.data.ok;
    } catch (err) {
      console.error("‚ùå Error verifying recipient:", err.response?.data || err.message);
      return false;
    }
  };

  // ============================
  // Handle send coin
  // ============================
  const handleSend = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!token || !userEmail) {
      setMessage("‚ùå User not logged in");
      return;
    }

    if (!selectedCoin || !sendAmount || !recipientEmail) {
      setMessage("‚ùå Please fill all fields");
      return;
    }

    if (recipientEmail.toLowerCase() === userEmail.toLowerCase()) {
      setMessage("‚ùå You cannot send coins to yourself!");
      return;
    }

    // Verify recipient
    const recipientExists = await verifyRecipient(recipientEmail.trim());
    if (!recipientExists) {
      setMessage("‚ùå Recipient does not exist on the app");
      return;
    }

    // Check sender balance
    const walletCoin = wallets.find((w) => w.coin === selectedCoin);
    if (!walletCoin || parseFloat(sendAmount) > parseFloat(walletCoin.amount)) {
      setMessage("‚ùå Insufficient coin balance");
      return;
    }

    // Send coin
    try {
      const res = await API.post(
        "/transactions/send",
        { coin: selectedCoin, amount: parseFloat(sendAmount), toEmail: recipientEmail.trim() },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("üîπ Send response:", res.data);
      setMessage("‚úÖ Transaction successful!");
      setSelectedCoin("");
      setSendAmount("");
      setRecipientEmail("");
      fetchWallets();
      fetchReceived();
    } catch (err) {
      console.error("‚ùå Send coin error:", err.response?.data || err.message);
      setMessage("‚ùå Failed to send coin. " + (err.response?.data?.message || ""));
    }
  };

  const tabButtonStyle = (active) => ({
    padding: "10px 20px",
    backgroundColor: active ? "#4caf50" : "#ccc",
    color: active ? "white" : "#333",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  });

  const inputStyle = { padding: "10px", borderRadius: "5px", border: "1px solid #ccc" };

  return (
    <div style={{ padding: "30px", maxWidth: "900px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>üí∏ Transactions</h2>

      {message && (
        <p style={{ textAlign: "center", color: message.startsWith("‚ùå") ? "red" : "green", fontWeight: "bold" }}>
          {message}
        </p>
      )}

      {/* Tabs */}
      <div style={{ display: "flex", justifyContent: "center", marginBottom: "20px", gap: "10px" }}>
        <button onClick={() => setTab("send")} style={tabButtonStyle(tab === "send")}>Send</button>
        <button onClick={() => setTab("received")} style={tabButtonStyle(tab === "received")}>Received</button>
      </div>

      {/* Send Form */}
      {tab === "send" && (
        <form onSubmit={handleSend} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          <select value={selectedCoin} onChange={(e) => setSelectedCoin(e.target.value)} required style={inputStyle}>
            <option value="">Select Coin</option>
            {wallets.map((w) => (
              <option key={w.coin} value={w.coin}>
                {w.coin} (Balance: {w.amount})
              </option>
            ))}
          </select>

          <input
            type="number"
            placeholder="Amount"
            value={sendAmount}
            onChange={(e) => setSendAmount(e.target.value)}
            required
            style={inputStyle}
          />

          <input
            type="email"
            placeholder="Recipient Email"
            value={recipientEmail}
            onChange={(e) => setRecipientEmail(e.target.value)}
            required
            style={inputStyle}
          />

          <button
            type="submit"
            disabled={recipientEmail.toLowerCase() === userEmail?.toLowerCase()}
            style={{
              ...inputStyle,
              backgroundColor: recipientEmail.toLowerCase() === userEmail?.toLowerCase() ? "#ccc" : "#4caf50",
              color: "white",
              fontWeight: "bold",
              cursor: recipientEmail.toLowerCase() === userEmail?.toLowerCase() ? "not-allowed" : "pointer",
              border: "none",
            }}
          >
            Send Coin
          </button>
        </form>
      )}

      {/* Received Transactions */}
      {tab === "received" && (
        <div>
          {receivedTx.length === 0 ? (
            <p style={{ textAlign: "center" }}>No received transactions yet</p>
          ) : (
            <ul style={{ listStyle: "none", padding: 0 }}>
              {receivedTx.map((tx) => (
                <li key={tx._id} style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  <strong>{tx.fromEmail}</strong> sent you <strong>{tx.amount}</strong> {tx.coin}
                  <br />
                  <small>{new Date(tx.createdAt).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}
