import React, { useState } from "react";
import API from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setMessage("❌ Please fill all fields");
      return;
    }

    try {
      const res = await API.post("/users/login", { email, password });

      // ✅ Save token and email
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userEmail", res.data.email);

      setMessage("✅ Login successful!");
      navigate("/transactions"); // redirect to transactions page
    } catch (err) {
      setMessage("❌ Login failed: " + (err.response?.data?.message || ""));
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
      <h2>Login</h2>
      {message && <p style={{ color: message.startsWith("❌") ? "red" : "green" }}>{message}</p>}
      <form onSubmit={handleLogin} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ padding: "10px", borderRadius: "5px", border: "1px solid #ccc" }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ padding: "10px", borderRadius: "5px", border: "1px solid #ccc" }}
        />
        <button
          type="submit"
          style={{ padding: "10px", borderRadius: "5px", border: "none", backgroundColor: "#4caf50", color: "white", cursor: "pointer" }}
        >
          Login
        </button>
      </form>
    </div>
  );
}
