import React, { useEffect, useState } from "react";
import API from "../services/api";

export default function Dashboard() {
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("‚ùå You are not logged in");
        return;
      }

      try {
        const res = await API.get("/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
      } catch (err) {
        console.error(err);
        setMessage("‚ùå Failed to fetch user data");
      }
    };

    fetchUser();
  }, []);

  return (
    <div style={{ padding: "20px", maxWidth: "900px", margin: "0 auto" }}>
      {/* Welcome Banner */}
      <div
        style={{
          background: "linear-gradient(135deg, #667eea, #764ba2)",
          color: "white",
          padding: "30px",
          borderRadius: "15px",
          textAlign: "center",
          marginBottom: "30px",
          boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
        }}
      >
        <h1 style={{ margin: "0 0 10px" }}>Welcome to Crypto Portfolio</h1>
        <p style={{ margin: 0 }}>Website created by Ashwin Waghela</p>
      </div>

      {/* User Info */}
      <div
        style={{
          padding: "20px",
          border: "1px solid #ddd",
          borderRadius: "10px",
          marginBottom: "30px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          textAlign: "center",
        }}
      >
        {message && <p style={{ color: "red" }}>{message}</p>}
        {user ? (
          <div>
            <p style={{ fontSize: "18px" }}>
              Welcome, <strong>{user.name}</strong>
            </p>
            <p style={{ fontSize: "16px", color: "#555" }}>Email: {user.email}</p>
          </div>
        ) : (
          <p>Loading user info...</p>
        )}
      </div>

      {/* Wallet Section Placeholder */}
      <div
        style={{
          padding: "20px",
          border: "1px solid #ddd",
          borderRadius: "10px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          textAlign: "center",
        }}
      >
        <h2>üí∞ Your Wallets</h2>
        <p>Wallet section will be displayed here.</p>
        {/* Later you can import your Wallet component here */}
        {/* <Wallet /> */}
      </div>
    </div>
  );
}
