// src/pages/Transactions.js
import React, { useState, useEffect, useCallback } from "react";
import API from "../services/api";

export default function Transactions() {
  const [transactions, setTransactions] = useState([]);
  const [activeTab, setActiveTab] = useState("received"); // default tab
  const [token] = useState(localStorage.getItem("token"));
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  // Fetch transactions
  const fetchTransactions = useCallback(async () => {
    if (!token) {
      setMessage("‚ùå Please login first");
      return;
    }

    try {
      setLoading(true);
      setMessage("");
      const res = await API.get("/transactions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Failed to fetch transactions");
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchTransactions();
  }, [fetchTransactions]);

  // Filter transactions based on tab
  const filteredTransactions = transactions.filter((t) =>
    activeTab === "sent" ? t.type === "sent" : t.type === "received"
  );

  return (
    <div style={{ padding: "30px", maxWidth: "900px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        üîÑ Transactions
      </h2>

      {message && (
        <p style={{ color: message.startsWith("‚ùå") ? "red" : "green", textAlign: "center" }}>
          {message}
        </p>
      )}

      {/* Tabs */}
      <div style={{ display: "flex", justifyContent: "center", marginBottom: "20px", gap: "10px" }}>
        <button
          onClick={() => setActiveTab("received")}
          style={{
            padding: "10px 20px",
            borderRadius: "5px",
            border: activeTab === "received" ? "2px solid #4caf50" : "1px solid #ccc",
            backgroundColor: activeTab === "received" ? "#e8f5e9" : "#fff",
            cursor: "pointer",
          }}
        >
          Received
        </button>
        <button
          onClick={() => setActiveTab("sent")}
          style={{
            padding: "10px 20px",
            borderRadius: "5px",
            border: activeTab === "sent" ? "2px solid #4caf50" : "1px solid #ccc",
            backgroundColor: activeTab === "sent" ? "#e8f5e9" : "#fff",
            cursor: "pointer",
          }}
        >
          Sent
        </button>
      </div>

      {/* Loading */}
      {loading && <p style={{ textAlign: "center" }}>‚è≥ Loading transactions...</p>}

      {/* Transactions List */}
      {!loading && filteredTransactions.length === 0 ? (
        <p style={{ textAlign: "center" }}>No transactions found</p>
      ) : (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
            gap: "20px",
          }}
        >
          {filteredTransactions.map((t) => (
            <div
              key={t._id}
              style={{
                padding: "15px",
                borderRadius: "10px",
                backgroundColor: "#fff",
                boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                textAlign: "center",
              }}
            >
              <h4 style={{ marginBottom: "10px" }}>
                {t.type === "sent" ? "‚¨Ü Sent" : "‚¨á Received"}
              </h4>
              <p>
                Coin: <strong>{t.coin.toUpperCase()}</strong>
              </p>
              <p>
                Amount: <strong>{t.amount}</strong>
              </p>
              <p>
                Date: {new Date(t.createdAt).toLocaleString()}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
