import React, { useEffect, useState } from "react";
import API from "../services/api";

export default function Wallet() {
  const [wallets, setWallets] = useState([]);
  const [message, setMessage] = useState("");
  const [coin, setCoin] = useState("");
  const [amount, setAmount] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchWallets = async () => {
      try {
        const res = await API.get("/wallets", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setWallets(res.data);
      } catch (err) {
        console.error(err);
        setMessage("❌ Failed to fetch wallet data");
      }
    };

    if (token) fetchWallets();
    else setMessage("❌ Please login first");
  }, [token]); // ✅ token is dependency

  // Add new wallet/coin
  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await API.post(
        "/wallets",
        { coin, amount: Number(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCoin("");
      setAmount("");
      // Refresh list after adding
      const res = await API.get("/wallets", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWallets(res.data);
    } catch (err) {
      console.error(err);
      setMessage("❌ Failed to add wallet");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>My Crypto Wallet</h2>
      {message && <p style={{ color: "red" }}>{message}</p>}

      <form onSubmit={handleAdd} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Coin name"
          value={coin}
          onChange={(e) => setCoin(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          Add Coin
        </button>
      </form>

      <h3>Your Holdings:</h3>
      {wallets.length === 0 ? (
        <p>No coins added yet</p>
      ) : (
        <ul>
          {wallets.map((w) => (
            <li key={w._id}>
              {w.coin} – {w.amount}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
