import React, { useState, useEffect, useCallback } from "react";
import API from "../services/api";
import axios from "axios";

export default function Transactions({ fetchWallets }) {
  const [type, setType] = useState("Send");
  const [coinsList, setCoinsList] = useState([]);
  const [selectedCoin, setSelectedCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [recipient, setRecipient] = useState(""); // for Receive type
  const [transactions, setTransactions] = useState([]);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");

  const token = localStorage.getItem("token");

  // Fetch available coins
  useEffect(() => {
    const fetchCoins = async () => {
      try {
        const res = await axios.get(
          "https://api.coingecko.com/api/v3/coins/list"
        );
        setCoinsList(res.data);
      } catch (err) {
        console.error("Failed to fetch coins list", err);
      }
    };
    fetchCoins();
  }, []);

  // Fetch transaction history
  const fetchTransactions = useCallback(async () => {
    if (!token) return;
    try {
      const res = await API.get("/transactions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
    }
  }, [token]);

  useEffect(() => {
    fetchTransactions();
  }, [fetchTransactions]);

  // Handle sending/receiving
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedCoin || !amount || (type === "Receive" && !recipient)) return;

    try {
      await API.post(
        "/transactions",
        { type, coin: selectedCoin, amount: Number(amount), recipient },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage(`‚úÖ ${type} successful!`);
      setAmount("");
      setSelectedCoin("");
      setRecipient("");

      fetchTransactions();
      fetchWallets(); // update wallet balances
    } catch (err) {
      console.error(err);
      setMessage(`‚ùå ${type} failed`);
    }
  };

  const filteredCoins = coinsList.filter((c) =>
    c.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: "20px", maxWidth: "900px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        üîÑ Transactions
      </h2>

      {message && (
        <p
          style={{
            color: message.startsWith("‚ùå") ? "red" : "green",
            textAlign: "center",
            fontWeight: "bold",
          }}
        >
          {message}
        </p>
      )}

      {/* Transaction Form */}
      <form
        onSubmit={handleSubmit}
        style={{
          display: "flex",
          gap: "10px",
          flexWrap: "wrap",
          justifyContent: "center",
          marginBottom: "30px",
        }}
      >
        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          style={{ padding: "10px", minWidth: "120px", borderRadius: "5px" }}
        >
          <option value="Send">Send</option>
          <option value="Receive">Receive</option>
        </select>

        <input
          type="text"
          placeholder="Search coin..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: "10px", flex: "1", minWidth: "150px", borderRadius: "5px" }}
        />

        <select
          value={selectedCoin}
          onChange={(e) => setSelectedCoin(e.target.value)}
          required
          style={{ padding: "10px", flex: "1", minWidth: "150px", borderRadius: "5px" }}
        >
          <option value="">Select Coin</option>
          {filteredCoins.slice(0, 50).map((coin) => (
            <option key={coin.id} value={coin.id}>
              {coin.name} ({coin.symbol.toUpperCase()})
            </option>
          ))}
        </select>

        {type === "Receive" && (
          <input
            type="text"
            placeholder="Recipient email"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            required
            style={{ padding: "10px", minWidth: "150px", borderRadius: "5px" }}
          />
        )}

        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
          style={{ padding: "10px", width: "120px", borderRadius: "5px" }}
        />

        <button
          type="submit"
          style={{
            padding: "10px 20px",
            backgroundColor: "#4caf50",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          {type === "Send" ? "‚û°Ô∏è Send" : "‚¨ÖÔ∏è Receive"}
        </button>
      </form>

      {/* Transaction History */}
      <h3>Transaction History</h3>
      {transactions.length === 0 ? (
        <p>No transactions yet.</p>
      ) : (
        <ul>
          {transactions
            .slice()
            .reverse()
            .map((t) => (
              <li key={t._id}>
                [{new Date(t.createdAt).toLocaleString()}]{" "}
                {t.type} {t.amount} {t.coin.toUpperCase()}{" "}
                {t.type === "Receive" && t.recipient ? `from ${t.recipient}` : ""}
              </li>
            ))}
        </ul>
      )}
    </div>
  );
}
