// src/components/Navbar.js
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import WalletActions from "./WalletActions";

const Navbar = ({ walletAddress, onUpdateBalance, user }) => {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [actionType, setActionType] = useState("send"); // 'send' or 'receive'

  const handleLogout = () => {
    // Clear auth state if needed
    navigate("/logout");
  };

  const openModal = (type) => {
    setActionType(type);
    setShowModal(true);
  };

  const closeModal = () => setShowModal(false);

  return (
    <>
      <nav className="bg-gradient-to-r from-blue-600 to-purple-600 text-white py-4 px-6 flex justify-between items-center shadow-md sticky top-0 z-50">
        {/* Logo */}
        <div className="flex items-center cursor-pointer" onClick={() => navigate("/dashboard")}>
          <img src="/logo192.png" alt="CryptoLogo" className="w-10 h-10 mr-2" />
          <h2 className="text-xl font-bold">CryptoPort</h2>
        </div>

        {/* Navigation Links */}
        <div className="flex items-center gap-4 flex-wrap">
          <Link
            to="/dashboard"
            className="text-white text-lg hover:text-gray-200 transition duration-300"
          >
            Dashboard
          </Link>
          <Link
            to="/wallets"
            className="text-white text-lg hover:text-gray-200 transition duration-300"
          >
            Wallet
          </Link>
          <Link
            to="/transactions"
            className="text-white text-lg hover:text-gray-200 transition duration-300"
          >
            Transactions
          </Link>

          {user ? (
            <>
              <button
                onClick={() => openModal("send")}
                className="bg-blue-500 px-4 py-2 rounded-lg text-white font-bold hover:bg-blue-600 transition"
              >
                ðŸš€ Send
              </button>
              <button
                onClick={() => openModal("receive")}
                className="bg-green-500 px-4 py-2 rounded-lg text-white font-bold hover:bg-green-600 transition"
              >
                ðŸ’° Receive
              </button>
              <button
                onClick={handleLogout}
                className="bg-red-500 px-4 py-2 rounded-lg text-white font-bold hover:bg-red-600 transition"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="text-white text-lg hover:text-gray-200 transition duration-300"
              >
                Login
              </Link>
              <Link
                to="/register"
                className="text-white text-lg hover:text-gray-200 transition duration-300"
              >
                Register
              </Link>
            </>
          )}
        </div>
      </nav>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white rounded-xl p-6 max-w-md w-full relative">
            <button
              onClick={closeModal}
              className="absolute top-2 right-2 text-gray-600 hover:text-gray-900 font-bold"
            >
              âœ–
            </button>

            <h2 className="text-xl font-bold mb-4 text-center">
              {actionType === "send" ? "Send Coins" : "Receive Coins"}
            </h2>

            <WalletActions
              walletAddress={walletAddress}
              onUpdateBalance={() => {
                onUpdateBalance?.();
                closeModal();
              }}
            />
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
