// src/pages/Transactions.js
import React, { useEffect, useState, useCallback } from "react";
import API from "../services/api";

export default function Transactions() {
  const [tab, setTab] = useState("send");
  const [wallets, setWallets] = useState([]);
  const [recipientEmail, setRecipientEmail] = useState("");
  const [selectedCoin, setSelectedCoin] = useState("");
  const [sendAmount, setSendAmount] = useState("");
  const [receivedTx, setReceivedTx] = useState([]);
  const [message, setMessage] = useState("");
  const [batch, setBatch] = useState([]); // { toEmail, coin, amount }
  const [batchResults, setBatchResults] = useState([]);
  const [submittingBatch, setSubmittingBatch] = useState(false);

  const token = localStorage.getItem("token");
  const userEmail = localStorage.getItem("userEmail");

  // Redirect if user is not logged in
  useEffect(() => {
    if (!userEmail || !token) {
      setMessage("❌ User not logged in");
    }
  }, [userEmail, token]);

  // Fetch wallets
  const fetchWallets = useCallback(async () => {
    if (!token) return;
    try {
      const res = await API.get("/wallets", { headers: { Authorization: `Bearer ${token}` } });
      setWallets(res.data);
    } catch (err) {
      console.error("❌ Wallet fetch error:", err.response?.data || err.message);
    }
  }, [token]);

  // Fetch received transactions
  const fetchReceived = useCallback(async () => {
    if (!token) return;
    try {
      const res = await API.get("/transactions/received", { headers: { Authorization: `Bearer ${token}` } });
      setReceivedTx(res.data);
    } catch (err) {
      console.error("❌ Received fetch error:", err.response?.data || err.message);
    }
  }, [token]);

  useEffect(() => {
    fetchWallets();
    fetchReceived();
  }, [fetchWallets, fetchReceived]);

  // ============================
  // Debug function: Verify recipient
  // ============================
  const verifyRecipient = async (email) => {
    try {
      console.log("🔹 Verifying recipient:", email);
      console.log("🔹 Using token:", token);

      const res = await API.post(
        "/transactions/verify-user",
        { email },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("🔹 Backend response:", res.data);
      return res.data.ok;
    } catch (err) {
      console.error("❌ Error verifying recipient:", err.response?.data || err.message);
      return false;
    }
  };

  // ============================
  // Handle send coin
  // ============================
  const handleSend = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!token || !userEmail) {
      setMessage("❌ User not logged in");
      return;
    }

    if (!selectedCoin || !sendAmount || !recipientEmail) {
      setMessage("❌ Please fill all fields");
      return;
    }

    if (recipientEmail.toLowerCase() === userEmail.toLowerCase()) {
      setMessage("❌ You cannot send coins to yourself!");
      return;
    }

    // Verify recipient
    const recipientExists = await verifyRecipient(recipientEmail.trim());
    if (!recipientExists) {
      setMessage("❌ Recipient does not exist on the app");
      return;
    }

    // Check sender balance
    const walletCoin = wallets.find((w) => w.coin === selectedCoin);
    if (!walletCoin || parseFloat(sendAmount) > parseFloat(walletCoin.amount)) {
      setMessage("❌ Insufficient coin balance");
      return;
    }

    // Send coin
    try {
      const res = await API.post(
        "/transactions/send",
        { coin: selectedCoin, amount: parseFloat(sendAmount), toEmail: recipientEmail.trim() },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log("🔹 Send response:", res.data);
      setMessage("✅ Transaction successful!");
      setSelectedCoin("");
      setSendAmount("");
      setRecipientEmail("");
      fetchWallets();
      fetchReceived();
    } catch (err) {
      console.error("❌ Send coin error:", err.response?.data || err.message);
      setMessage("❌ Failed to send coin. " + (err.response?.data?.message || ""));
    }
  };

  // Batch helpers
  const addToBatch = () => {
    if (!selectedCoin || !sendAmount || !recipientEmail) {
      setMessage("❌ Please fill coin, amount and recipient");
      return;
    }
    if (recipientEmail.toLowerCase() === userEmail?.toLowerCase()) {
      setMessage("❌ Cannot add self as recipient");
      return;
    }
    setBatch((prev) => {
      const existing = prev.find((p) => p.toEmail === recipientEmail && p.coin === selectedCoin);
      if (existing) {
        return prev.map((p) => (p.toEmail === recipientEmail && p.coin === selectedCoin ? { ...p, amount: Number(p.amount) + Number(sendAmount) } : p));
      }
      return [...prev, { toEmail: recipientEmail, coin: selectedCoin, amount: Number(sendAmount) }];
    });
    setMessage("Added to batch");
    setSelectedCoin("");
    setSendAmount("");
    setRecipientEmail("");
  };

  const removeFromBatch = (index) => {
    setBatch((prev) => prev.filter((_, i) => i !== index));
  };

  const submitBatchTransactions = async () => {
    if (!batch.length) return setMessage("❌ Batch is empty");
    setSubmittingBatch(true);
    try {
      const token = localStorage.getItem("token");
      const res = await API.post("/transactions/send-batch", { items: batch }, { headers: { Authorization: `Bearer ${token}` } });
      setBatchResults(res.data.results || []);
      setMessage(res.data.message || "✅ Batch processed");
      setBatch([]);
      fetchWallets();
      fetchReceived();
    } catch (err) {
      console.error("Batch send failed", err.response?.data || err.message);
      setMessage("❌ Batch send failed: " + (err.response?.data?.message || err.message));
    } finally {
      setSubmittingBatch(false);
    }
  };

  const tabButtonStyle = (active) => ({
    padding: "10px 20px",
    backgroundColor: active ? "#4caf50" : "#ccc",
    color: active ? "white" : "#333",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  });

  const inputStyle = { padding: "10px", borderRadius: "5px", border: "1px solid #ccc" };

  return (
    <div style={{ padding: "30px", maxWidth: "900px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>💸 Transactions</h2>

      {message && (
        <p style={{ textAlign: "center", color: message.startsWith("❌") ? "red" : "green", fontWeight: "bold" }}>
          {message}
        </p>
      )}

      {/* Tabs */}
      <div style={{ display: "flex", justifyContent: "center", marginBottom: "20px", gap: "10px" }}>
        <button onClick={() => setTab("send")} style={tabButtonStyle(tab === "send")}>Send</button>
        <button onClick={() => setTab("received")} style={tabButtonStyle(tab === "received")}>Received</button>
      </div>

      {/* Send Form */}
      {tab === "send" && (
        <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          <div>
            <select value={selectedCoin} onChange={(e) => setSelectedCoin(e.target.value)} required style={inputStyle}>
              <option value="">Select Coin</option>
              {wallets.map((w) => (
                <option key={w.coin} value={w.coin}>
                  {w.coin} (Balance: {w.amount})
                </option>
              ))}
            </select>
          </div>

          <div>
            <input
              type="number"
              placeholder="Amount"
              value={sendAmount}
              onChange={(e) => setSendAmount(e.target.value)}
              required
              style={inputStyle}
            />
          </div>

          <div>
            <input
              type="email"
              placeholder="Recipient Email"
              value={recipientEmail}
              onChange={(e) => setRecipientEmail(e.target.value)}
              required
              style={inputStyle}
            />
          </div>

          <div style={{ display: "flex", gap: 8 }}>
            <button
              onClick={addToBatch}
              style={{ ...inputStyle, backgroundColor: "#2563eb", color: "white", border: "none", cursor: "pointer" }}
            >
              Add to batch
            </button>
            <button
              type="button"
              onClick={handleSend}
              disabled={recipientEmail.toLowerCase() === userEmail?.toLowerCase()}
              style={{
                ...inputStyle,
                backgroundColor: recipientEmail.toLowerCase() === userEmail?.toLowerCase() ? "#ccc" : "#4caf50",
                color: "white",
                fontWeight: "bold",
                cursor: recipientEmail.toLowerCase() === userEmail?.toLowerCase() ? "not-allowed" : "pointer",
                border: "none",
              }}
            >
              Send Coin
            </button>
            <button
              type="button"
              onClick={submitBatchTransactions}
              disabled={submittingBatch || batch.length === 0}
              style={{ padding: "10px", borderRadius: 5, border: "none", background: submittingBatch ? "#9ca3af" : "#16a34a", color: "#fff", cursor: submittingBatch || batch.length === 0 ? "not-allowed" : "pointer" }}
            >
              {submittingBatch ? "Submitting..." : `Submit batch (${batch.length})`}
            </button>
          </div>

          {/* Batch preview */}
          {batch.length > 0 && (
            <div style={{ marginTop: 10 }}>
              <h4>Pending batch</h4>
              <ul style={{ listStyle: "none", padding: 0 }}>
                {batch.map((b, i) => (
                  <li key={i} style={{ padding: 8, borderBottom: "1px solid #eee", display: "flex", justifyContent: "space-between" }}>
                    <div>{b.toEmail} — {b.amount} {b.coin}</div>
                    <div><button onClick={() => removeFromBatch(i)} style={{ background: "#ef4444", color: "#fff", border: "none", padding: "6px 10px", borderRadius: 6 }}>Remove</button></div>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      {/* Received Transactions */}
      {tab === "received" && (
        <div>
          {receivedTx.length === 0 ? (
            <p style={{ textAlign: "center" }}>No received transactions yet</p>
          ) : (
            <ul style={{ listStyle: "none", padding: 0 }}>
              {receivedTx.map((tx) => (
                <li key={tx._id} style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  <strong>{tx.fromEmail}</strong> sent you <strong>{tx.amount}</strong> {tx.coin}
                  <br />
                  <small>{new Date(tx.createdAt).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}
