import React, { useEffect, useState } from "react";
import API from "../services/api";
import axios from "axios";

export default function Wallet() {
  const [wallets, setWallets] = useState([]);
  const [message, setMessage] = useState("");
  const [coin, setCoin] = useState("");
  const [amount, setAmount] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchWallets = async () => {
      try {
        // Fetch wallets from backend
        const res = await API.get("/wallets", {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Fetch live prices from CoinGecko
        const updatedWallets = await Promise.all(
          res.data.map(async (w) => {
            try {
              const coinRes = await axios.get(
                `https://api.coingecko.com/api/v3/simple/price?ids=${w.coin.toLowerCase()}&vs_currencies=usd`
              );
              const valueUSD = coinRes.data[w.coin.toLowerCase()]?.usd || 0;
              return { ...w, valueUSD: (valueUSD * w.amount).toFixed(2) };
            } catch {
              return { ...w, valueUSD: "N/A" };
            }
          })
        );

        setWallets(updatedWallets);
      } catch (err) {
        console.error(err);
        setMessage("❌ Failed to fetch wallet data");
      }
    };

    if (token) fetchWallets();
    else setMessage("❌ Please login first");
  }, [token]); // ✅ only depends on token

  // Add new wallet/coin
  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await API.post(
        "/wallets",
        { coin, amount: Number(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCoin("");
      setAmount("");
      // Refresh wallet list after adding
      const res = await API.get("/wallets", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedWallets = await Promise.all(
        res.data.map(async (w) => {
          try {
            const coinRes = await axios.get(
              `https://api.coingecko.com/api/v3/simple/price?ids=${w.coin.toLowerCase()}&vs_currencies=usd`
            );
            const valueUSD = coinRes.data[w.coin.toLowerCase()]?.usd || 0;
            return { ...w, valueUSD: (valueUSD * w.amount).toFixed(2) };
          } catch {
            return { ...w, valueUSD: "N/A" };
          }
        })
      );

      setWallets(updatedWallets);
    } catch (err) {
      console.error(err);
      setMessage("❌ Failed to add wallet");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>My Crypto Wallet</h2>
      {message && <p style={{ color: "red" }}>{message}</p>}

      <form onSubmit={handleAdd} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Coin name (e.g., bitcoin)"
          value={coin}
          onChange={(e) => setCoin(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          Add Coin
        </button>
      </form>

      <h3>Your Holdings:</h3>
      {wallets.length === 0 ? (
        <p>No coins added yet</p>
      ) : (
        <table border="1" cellPadding="5">
          <thead>
            <tr>
              <th>Coin</th>
              <th>Amount</th>
              <th>Value (USD)</th>
            </tr>
          </thead>
          <tbody>
            {wallets.map((w) => (
              <tr key={w._id}>
                <td>{w.coin}</td>
                <td>{w.amount}</td>
                <td>{w.valueUSD}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
