// src/pages/Transactions.js
import React, { useState, useEffect } from "react";
import API from "../services/api";

export default function Transactions() {
  const [activeTab, setActiveTab] = useState("received"); // "send" or "received"
  const [transactions, setTransactions] = useState([]);
  const [coin, setCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");

  // Fetch all transactions
  const fetchTransactions = async () => {
    if (!token) return setMessage("âŒ Please login first");
    try {
      const res = await API.get("/transactions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
      setMessage("âŒ Failed to fetch transactions");
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  // Send coin
  const handleSend = async (e) => {
    e.preventDefault();
    if (!coin || !amount) return;

    try {
      await API.post(
        "/transactions/send",
        { coin, amount: Number(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(`âœ… Successfully sent ${amount} ${coin.toUpperCase()}`);
      setCoin("");
      setAmount("");
      fetchTransactions();
    } catch (err) {
      console.error(err);
      setMessage("âŒ Failed to send coin");
    }
  };

  // Filter transactions based on tab
  const filteredTransactions = transactions.filter((t) =>
    activeTab === "send" ? t.type === "sent" : t.type === "received"
  );

  return (
    <div style={{ padding: "30px", maxWidth: "900px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        ðŸ”„ Transactions
      </h2>

      {message && (
        <p
          style={{
            textAlign: "center",
            color: message.startsWith("âŒ") ? "red" : "green",
          }}
        >
          {message}
        </p>
      )}

      {/* Tabs */}
      <div style={{ display: "flex", justifyContent: "center", gap: "10px", marginBottom: "20px" }}>
        <button
          onClick={() => setActiveTab("send")}
          style={{
            padding: "10px 20px",
            borderRadius: "5px",
            border: activeTab === "send" ? "2px solid #4caf50" : "1px solid #ccc",
            backgroundColor: activeTab === "send" ? "#e8f5e9" : "#fff",
            cursor: "pointer",
          }}
        >
          Send
        </button>
        <button
          onClick={() => setActiveTab("received")}
          style={{
            padding: "10px 20px",
            borderRadius: "5px",
            border: activeTab === "received" ? "2px solid #4caf50" : "1px solid #ccc",
            backgroundColor: activeTab === "received" ? "#e8f5e9" : "#fff",
            cursor: "pointer",
          }}
        >
          Received
        </button>
      </div>

      {/* Send form */}
      {activeTab === "send" && (
        <form
          onSubmit={handleSend}
          style={{ display: "flex", gap: "10px", justifyContent: "center", marginBottom: "30px" }}
        >
          <input
            type="text"
            placeholder="Coin"
            value={coin}
            onChange={(e) => setCoin(e.target.value)}
            required
            style={{ padding: "10px", borderRadius: "5px", border: "1px solid #ccc" }}
          />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
            style={{ padding: "10px", borderRadius: "5px", border: "1px solid #ccc" }}
          />
          <button
            type="submit"
            style={{
              padding: "10px 20px",
              backgroundColor: "#4caf50",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Send
          </button>
        </form>
      )}

      {/* Transactions list */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
          gap: "20px",
        }}
      >
        {filteredTransactions.length === 0 ? (
          <p style={{ textAlign: "center" }}>No transactions found</p>
        ) : (
          filteredTransactions.map((t) => (
            <div
              key={t._id}
              style={{
                padding: "15px",
                borderRadius: "10px",
                backgroundColor: "#fff",
                boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                textAlign: "center",
              }}
            >
              <h4>{t.type === "sent" ? "â¬† Sent" : "â¬‡ Received"}</h4>
              <p>Coin: <strong>{t.coin.toUpperCase()}</strong></p>
              <p>Amount: <strong>{t.amount}</strong></p>
              <p>Date: {new Date(t.createdAt).toLocaleString()}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
