import React from "react";
import { useNavigate, Link } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Navigate to the live Logout page which clears storage and redirects
    navigate("/logout");
  };

  return (
    <nav className="bg-gradient-to-r from-blue-600 to-purple-600 text-white py-4 px-6 flex justify-between items-center shadow-md sticky top-0 z-10">
      {/* Logo */}
      <div className="flex items-center cursor-pointer" onClick={() => navigate("/dashboard")}>
        <img
          src="/logo192.png" // Replace with your custom logo path
          alt="CryptoLogo"
          className="w-10 h-10 mr-2"
        />
        <h2 className="text-xl font-bold">CryptoPort</h2>
      </div>

      {/* Navigation Links */}
      <div className="flex gap-6">
        <Link
          to="/dashboard"
          className="text-white text-lg hover:text-gray-200 transition duration-300"
        >
          Dashboard
        </Link>
        <Link
          to="/wallets"
          className="text-white text-lg hover:text-gray-200 transition duration-300"
        >
          Wallet
        </Link>
        <Link
          to="/register"
          className="text-white text-lg hover:text-gray-200 transition duration-300"
        >
          Register
        </Link>
        <Link
          to="/login"
          className="text-white text-lg hover:text-gray-200 transition duration-300"
        >
          Login
        </Link>

        {/* Logout Button */}
        <button
          onClick={handleLogout}
          className="bg-red-500 px-4 py-2 rounded-lg text-white font-bold hover:bg-red-600 transition"
        >
          Logout
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
