import axios from "axios";

// Use REACT_APP_API_URL when set (useful for ngrok/public URLs), otherwise default to localhost
const BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const API = axios.create({
  baseURL: BASE_URL,
});

export default API;
export const fetchCoinsList = async () => {
  try {
    const res = await axios.get("https://api.coingecko.com/api/v3/coins/list");
    return res.data; // array of { id, symbol, name }
  } catch (err) {
    console.error("Error fetching coins list:", err);
    return [];
  }
};
