import React, { useState, useEffect } from "react";
import API from "../services/api";

export default function Transactions() {
  const [tab, setTab] = useState("send"); // "send" or "received"
  const [wallets, setWallets] = useState([]);
  const [recipient, setRecipient] = useState("");
  const [coin, setCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [receivedTxs, setReceivedTxs] = useState([]);

  const token = localStorage.getItem("token");

  // Fetch user's wallets for send dropdown
  const fetchWallets = async () => {
    if (!token) return;
    try {
      const res = await API.get("/wallets", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWallets(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Fetch received transactions
  const fetchReceived = async () => {
    if (!token) return;
    try {
      const res = await API.get("/transactions/received", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReceivedTxs(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchWallets();
    if (tab === "received") fetchReceived();
  }, [tab]);

  // Send coin handler
  const handleSend = async (e) => {
    e.preventDefault();
    if (!recipient || !coin || !amount) return;
    try {
      const res = await API.post(
        "/transactions/send",
        { recipient, coin, amount: Number(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message || "Coin sent successfully!");
      setRecipient("");
      setCoin("");
      setAmount("");
      fetchWallets(); // update wallets after sending
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "Failed to send coin");
    }
  };

  return (
    <div style={{ maxWidth: "900px", margin: "0 auto", padding: "20px" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>💸 Transactions</h2>

      {/* Tab buttons */}
      <div style={{ display: "flex", justifyContent: "center", gap: "20px", marginBottom: "20px" }}>
        <button
          onClick={() => setTab("send")}
          style={{
            padding: "10px 20px",
            borderRadius: "5px",
            border: "none",
            cursor: "pointer",
            backgroundColor: tab === "send" ? "#4caf50" : "#ccc",
            color: tab === "send" ? "white" : "#333",
            fontWeight: "bold",
          }}
        >
          Send
        </button>
        <button
          onClick={() => setTab("received")}
          style={{
            padding: "10px 20px",
            borderRadius: "5px",
            border: "none",
            cursor: "pointer",
            backgroundColor: tab === "received" ? "#4caf50" : "#ccc",
            color: tab === "received" ? "white" : "#333",
            fontWeight: "bold",
          }}
        >
          Received
        </button>
      </div>

      {message && (
        <p style={{ color: message.startsWith("❌") ? "red" : "green", textAlign: "center", fontWeight: "bold" }}>
          {message}
        </p>
      )}

      {/* Send tab */}
      {tab === "send" && (
        <form
          onSubmit={handleSend}
          style={{ display: "flex", flexDirection: "column", gap: "15px", maxWidth: "500px", margin: "0 auto" }}
        >
          <select
            value={coin}
            onChange={(e) => setCoin(e.target.value)}
            required
            style={{ padding: "10px", borderRadius: "5px", border: "1px solid #ccc" }}
          >
            <option value="">Select Coin</option>
            {wallets.map((w) => (
              <option key={w._id} value={w.coin}>
                {w.coin.toUpperCase()} (Available: {w.amount})
              </option>
            ))}
          </select>

          <input
            type="text"
            placeholder="Recipient email"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            required
            style={{ padding: "10px", borderRadius: "5px", border: "1px solid #ccc" }}
          />

          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
            min="0"
            step="any"
            style={{ padding: "10px", borderRadius: "5px", border: "1px solid #ccc" }}
          />

          <button
            type="submit"
            style={{
              padding: "10px 20px",
              borderRadius: "5px",
              border: "none",
              backgroundColor: "#4caf50",
              color: "white",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            Send Coin
          </button>
        </form>
      )}

      {/* Received tab */}
      {tab === "received" && (
        <div style={{ marginTop: "20px" }}>
          {receivedTxs.length === 0 ? (
            <p style={{ textAlign: "center" }}>No received transactions yet.</p>
          ) : (
            <table style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr>
                  <th style={{ border: "1px solid #ccc", padding: "8px" }}>From</th>
                  <th style={{ border: "1px solid #ccc", padding: "8px" }}>Coin</th>
                  <th style={{ border: "1px solid #ccc", padding: "8px" }}>Amount</th>
                  <th style={{ border: "1px solid #ccc", padding: "8px" }}>Date</th>
                </tr>
              </thead>
              <tbody>
                {receivedTxs.map((tx) => (
                  <tr key={tx._id}>
                    <td style={{ border: "1px solid #ccc", padding: "8px" }}>{tx.sender.name} ({tx.sender.email})</td>
                    <td style={{ border: "1px solid #ccc", padding: "8px" }}>{tx.coin.toUpperCase()}</td>
                    <td style={{ border: "1px solid #ccc", padding: "8px" }}>{tx.amount}</td>
                    <td style={{ border: "1px solid #ccc", padding: "8px" }}>{new Date(tx.createdAt).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
}
