import React, { useEffect, useState } from "react";
import API from "../services/api";
import axios from "axios";

export default function Wallet() {
  const [wallets, setWallets] = useState([]);
  const [coinsList, setCoinsList] = useState([]);
  const [selectedCoin, setSelectedCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");

  const token = localStorage.getItem("token");

  // Fetch wallets and coin prices
  const fetchWallets = async () => {
    if (!token) {
      setMessage("❌ Please login first");
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setMessage("");

      const res = await API.get("/wallets", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedWallets = await Promise.all(
        res.data.map(async (w) => {
          let valueUSD = "N/A";
          let image = null;

          if (w.coin) {
            try {
              const coinRes = await axios.get(
                `https://api.coingecko.com/api/v3/coins/${w.coin.toLowerCase()}`
              );
              valueUSD = coinRes.data.market_data.current_price.usd
                ? (coinRes.data.market_data.current_price.usd * w.amount).toFixed(2)
                : "N/A";
              image = coinRes.data.image.thumb;
            } catch {
              valueUSD = "N/A";
            }
          }

          return { ...w, valueUSD, image };
        })
      );

      // Sort by highest USD value first
      updatedWallets.sort((a, b) => (b.valueUSD === "N/A" ? 0 : b.valueUSD) - (a.valueUSD === "N/A" ? 0 : a.valueUSD));

      setWallets(updatedWallets);
    } catch (err) {
      console.error(err);
      setMessage("❌ Failed to fetch wallet data");
    } finally {
      setLoading(false);
    }
  };

  // Auto-refresh every 10 seconds
  useEffect(() => {
    fetchWallets();
    const interval = setInterval(fetchWallets, 10000);
    return () => clearInterval(interval);
  }, [token]);

  // Fetch coins list for dropdown
  useEffect(() => {
    const fetchCoins = async () => {
      try {
        const res = await axios.get("https://api.coingecko.com/api/v3/coins/list");
        setCoinsList(res.data);
      } catch (err) {
        console.error("Failed to fetch coins list", err);
      }
    };
    fetchCoins();
  }, []);

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!selectedCoin || !amount) return;

    try {
      await API.post(
        "/wallets",
        { coin: selectedCoin, amount: Number(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSelectedCoin("");
      setAmount("");
      setMessage("✅ Coin added successfully");
      fetchWallets();
    } catch (err) {
      console.error(err);
      setMessage("❌ Failed to add wallet");
    }
  };

  const filteredCoins = coinsList.filter((c) =>
    c.name.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) return <p>Loading wallet data...</p>;

  return (
    <div style={{ padding: "20px", maxWidth: "900px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>💰 My Crypto Wallet</h2>
      {message && (
        <p
          style={{
            color: message.startsWith("❌") ? "red" : "green",
            textAlign: "center",
          }}
        >
          {message}
        </p>
      )}

      {/* Add Coin Form */}
      <form
        onSubmit={handleAdd}
        style={{
          display: "flex",
          gap: "10px",
          flexWrap: "wrap",
          justifyContent: "center",
          marginBottom: "30px",
        }}
      >
        <input
          type="text"
          placeholder="Search coin..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: "10px", flex: "1" }}
        />
        <select
          value={selectedCoin}
          onChange={(e) => setSelectedCoin(e.target.value)}
          required
          style={{ padding: "10px", flex: "1" }}
        >
          <option value="">Select Coin</option>
          {filteredCoins.slice(0, 50).map((coin) => (
            <option key={coin.id} value={coin.id}>
              {coin.name} ({coin.symbol.toUpperCase()})
            </option>
          ))}
        </select>

        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
          style={{ padding: "10px", width: "100px" }}
        />

        <button
          type="submit"
          style={{
            padding: "10px 20px",
            backgroundColor: "#4caf50",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Add Coin
        </button>
      </form>

      {/* Wallet Cards */}
      {wallets.length === 0 ? (
        <p style={{ textAlign: "center" }}>No coins added yet</p>
      ) : (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(180px, 1fr))",
            gap: "20px",
          }}
        >
          {wallets.map((w) => (
            <div
              key={w._id || w.coin}
              style={{
                padding: "15px",
                border: "1px solid #ccc",
                borderRadius: "10px",
                textAlign: "center",
                boxShadow: "0 4px 8px rgba(0,0,0,0.15)",
                transition: "transform 0.2s",
              }}
            >
              {w.image && <img src={w.image} alt={w.coin} width="40" height="40" />}
              <h4 style={{ margin: "10px 0" }}>{w.coin?.toUpperCase() || "N/A"}</h4>
              <p>Amount: {w.amount || 0}</p>
              <p>
                Value (USD): {w.valueUSD === "N/A" ? "N/A" : `$${w.valueUSD}`}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
