// src/pages/Transactions.js
import React, { useState, useEffect } from "react";
import API from "../services/api";

export default function Transactions() {
  const [tab, setTab] = useState("send"); // send | received
  const [wallets, setWallets] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [recipient, setRecipient] = useState("");
  const [coin, setCoin] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const token = localStorage.getItem("token");
  const userEmail = localStorage.getItem("email"); // must be set on login/register

  // Fetch wallets
  const fetchWallets = async () => {
    if (!token) return;
    try {
      const res = await API.get("/wallets", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWallets(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Fetch transactions
  const fetchTransactions = async () => {
    if (!token) return;
    try {
      const res = await API.get("/transactions", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchWallets();
    fetchTransactions();
  }, []);

  // Handle send coin
  const handleSend = async (e) => {
    e.preventDefault();
    if (!recipient || !coin || !amount) {
      setMessage("❌ Please fill all fields!");
      return;
    }

    if (!userEmail) {
      setMessage("❌ User email not found. Please login again!");
      return;
    }

    if (recipient.toLowerCase() === userEmail.toLowerCase()) {
      setMessage("❌ You cannot send coins to yourself!");
      return;
    }

    try {
      const res = await API.post(
        "/transactions/send",
        { recipient, coin, amount: Number(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message || "✅ Coin sent successfully!");
      setRecipient("");
      setCoin("");
      setAmount("");
      fetchWallets();
      fetchTransactions();
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "❌ Failed to send coin");
    }
  };

  return (
    <div style={{ maxWidth: "900px", margin: "0 auto", padding: "20px" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>💱 Transactions</h2>

      {/* Tab Selector */}
      <div style={{ display: "flex", justifyContent: "center", gap: "10px", marginBottom: "20px" }}>
        <button
          onClick={() => setTab("send")}
          style={{
            padding: "10px 20px",
            backgroundColor: tab === "send" ? "#4caf50" : "#ccc",
            color: tab === "send" ? "white" : "#333",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Send
        </button>
        <button
          onClick={() => setTab("received")}
          style={{
            padding: "10px 20px",
            backgroundColor: tab === "received" ? "#4caf50" : "#ccc",
            color: tab === "received" ? "white" : "#333",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Received
        </button>
      </div>

      {/* Message */}
      {message && (
        <p style={{ color: message.startsWith("❌") ? "red" : "green", textAlign: "center", fontWeight: "bold" }}>
          {message}
        </p>
      )}

      {/* Send Form */}
      {tab === "send" && (
        <form
          onSubmit={handleSend}
          style={{
            display: "flex",
            gap: "10px",
            flexWrap: "wrap",
            justifyContent: "center",
            marginBottom: "30px",
          }}
        >
          <select
            value={coin}
            onChange={(e) => setCoin(e.target.value)}
            required
            style={{ padding: "10px", minWidth: "150px", borderRadius: "5px", border: "1px solid #ccc" }}
          >
            <option value="">Select Coin</option>
            {wallets.map((w) => (
              <option key={w._id} value={w.coin}>
                {w.coin.toUpperCase()} (Available: {w.amount})
              </option>
            ))}
          </select>

          <input
            type="text"
            placeholder="Recipient Email"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            required
            style={{ padding: "10px", minWidth: "200px", borderRadius: "5px", border: "1px solid #ccc" }}
          />

          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
            style={{ padding: "10px", width: "120px", borderRadius: "5px", border: "1px solid #ccc" }}
          />

          <button
            type="submit"
            style={{
              padding: "10px 20px",
              backgroundColor: "#4caf50",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Send
          </button>
        </form>
      )}

      {/* Received Transactions */}
      {tab === "received" && (
        <div>
          {transactions.filter((t) => t.recipient === userEmail).length === 0 ? (
            <p style={{ textAlign: "center" }}>No received transactions yet.</p>
          ) : (
            <ul>
              {transactions
                .filter((t) => t.recipient === userEmail)
                .map((t) => (
                  <li key={t._id}>
                    {t.sender} sent {t.amount} {t.coin.toUpperCase()} on {new Date(t.createdAt).toLocaleString()}
                  </li>
                ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}
